#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/seq-run-2/52943_BH7T77BCX2_CAGCATC_S6_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/seq-run-2/52943_BH7T77BCX2_CAGCATC_S6_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/seq-run-1/52943_BH7T33BCX2_CAGCATC_S6_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Pseudomys-hermannsburgensis/Pseudomys-hermannsburgensis-flagstats.txt
