#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/seq-run-4/53925_HTVLWBCX2_ACTATCA_S13_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/seq-run-4/53925_HTVLWBCX2_ACTATCA_S13_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/seq-run-2/52953_BH7T77BCX2_GTACTAT_S16_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/seq-run-2/52953_BH7T77BCX2_GTACTAT_S16_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/seq-run-3/53925_HTVLKBCX2_ACTATCA_S13_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/seq-run-3/53925_HTVLKBCX2_ACTATCA_S13_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/seq-run-1/52953_BH7T33BCX2_GTACTAT_S16_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-higginsi/Pseudomys-higginsi-flagstats.txt
