#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/seq-run-2/52942_BH7T77BCX2_AGAATTA_S5_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/seq-run-2/52942_BH7T77BCX2_AGAATTA_S5_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/seq-run-1/52942_BH7T33BCX2_AGAATTA_S5_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-pilligaensis/Pseudomys-pilligaensis-flagstats.txt
