#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/hiseq-paired-no-WGA/exulans_GCATTG_L007_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/hiseq-paired-no-WGA/exulans_GCATTG_L008_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/hiseq-paired/exulansWGA_TGACGT_L008_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/hiseq-paired/exulans-WGA_TGACGT_L007_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/hiseq2500-paired-no-WGA/lane2_GCATTG_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/hiseq2500-paired/lane2_TGACGT_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-exulans/Rattus-exulans-flagstats.txt
