#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/hiseq-paired/lutreolus-WGA_CAATAT_L007_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/hiseq-paired/lutreolusWGA_CAATAT_L008_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/hiseq2500-paired/lane2_CAATAT_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-lutreolus/Rattus-lutreolus-flagstats.txt
