#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/hiseq-paired/praetorWGA_CAGTAC_L008_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/hiseq-paired/praetor-WGA_CAGTAC_L007_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/hiseq2500-paired/lane2_CAGTAC_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Rattus-praetor/Rattus-praetor-flagstats.txt
