#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/hiseq-paired/vilosissimusWGA_AGATAG_L008_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/hiseq-paired/vilosissimus-WGA_AGATAG_L007_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/hiseq2500-paired/lane2_AGATAG_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Rattus-villosissimus/Rattus-villosissimus-flagstats.txt
