#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/hiseq4000-paired-2/REC19_CKDL190143345-1a-D707-AK1545_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-callitrichus/Taeromys-callitrichus-flagstats.txt
