#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/hiseq4000-paired-2/REC20_CKDL190143345-1a-D708-AK1545_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Taeromys-taerae/Taeromys-taerae-flagstats.txt
