#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/hiseq4000-paired-2/REC21_CKDL190143345-1a-D709-AK1545_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Tarsomys-apoensis/Tarsomys-apoensis-flagstats.txt
