#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/hiseq4000-paired-1/SM078_CKDL190143344-1a-D707-AK1780_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Uromys-caudimaculatus/Uromys-caudimaculatus-flagstats.txt
