#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/hiseq4000-paired-2/SM079_CKDL190143345-1a-D708-AK1780_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Vandeleuria-oleracea/Vandeleuria-oleracea-flagstats.txt
