#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/hiseq4000-paired-1/SM010_CKDL190143343-1a-D701-AK1680_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Xenuromys-barbatus/Xenuromys-barbatus-flagstats.txt
