#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/hiseq4000-paired-1/SM080_CKDL190143343-1a-D709-AK1780_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Xeromys-myoides/Xeromys-myoides-flagstats.txt
