#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/seq-run-2/52946_BH7T77BCX2_AACGACC_S9_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/seq-run-2/52946_BH7T77BCX2_AACGACC_S9_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/seq-run-1/52946_BH7T33BCX2_AACGACC_S9_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-argurus/Zyzomys-argurus-flagstats.txt
