#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/seq-run-2/52947_BH7T77BCX2_CCAGCGG_S10_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/seq-run-2/52947_BH7T77BCX2_CCAGCGG_S10_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/seq-run-1/52947_BH7T33BCX2_CCAGCGG_S10_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-maini/Zyzomys-maini-flagstats.txt
