#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/seq-run-2/52949_BH7T77BCX2_TGGCAAT_S12_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/seq-run-2/52949_BH7T77BCX2_TGGCAAT_S12_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/seq-run-1/52949_BH7T33BCX2_TGGCAAT_S12_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-palatalis/Zyzomys-palatalis-flagstats.txt
