#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/seq-run-2/52948_BH7T77BCX2_TAGTTCC_S11_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/seq-run-2/52948_BH7T77BCX2_TAGTTCC_S11_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/seq-run-1/52948_BH7T33BCX2_TAGTTCC_S11_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Zyzomys-pedunculatus/Zyzomys-pedunculatus-flagstats.txt
